import time
import random
import ctypes
from colorama import init, Fore, Style

# Initialize colorama
init()
# Set the color to green
print(Fore.GREEN)

# Set the start time
start_time = time.time()

# Generate random letters for 1000 seconds
while time.time() - start_time < 1000:
    # Generate a random letter and print it
    letter = chr(random.randint(97, 122))
    print(letter, end='', flush=True)
    # Wait for a small random amount of time before generating the next letter
    time.sleep(random.uniform(0., 0.))

# Reset the color to default
print(Style.RESET_ALL)

# Hide the command prompt window's border
ctypes.windll.user32.SetWindowLongW(ctypes.windll.kernel32.GetConsoleWindow(), 
                                    ctypes.c_int(-16), 
                                    ctypes.c_long(524288 | 32))

# Keep the window open until the user presses a key
input("Press any key to exit.")
